%% Unit aperture definition for the wovenfunction [Coords_aper]=cell(x_ini, y_ini,aper)% allocationCoords_aper  = zeros(5,2);Coords_aper_cell(1,1) = x_ini;Coords_aper(1,2) = y_ini;Coords_aper(2,1) = x_ini;Coords_aper(2,2) = y_ini + aper;Coords_aper(3,1) = x_ini + wire_size;Coords_aper(3,2) = Coords_aper(2,2);Coords_aper(4,1) = Coords_aper(3,1);Coords_aper(4,2) = Coords_aper(3,2) - aper;Coords_aper(5,1) = x_ini;Coords_aper(5,2) = y_ini;end