%% Unit cell definition for the wovenfunction [Coords_cell]=cell(x_ini, y_ini,wire_size)% allocationCoords_cell  = zeros(5,2);Coords_cell(1,1) = x_ini;Coords_cell(1,2) = y_ini;Coords_cell(2,1) = x_ini;Coords_cell(2,2) = y_ini + wire_size;Coords_cell(3,1) = x_ini + wire_size;Coords_cell(3,2) = Coords_cell(2,2);Coords_cell(4,1) = Coords_cell(3,1);Coords_cell(4,2) = Coords_cell(3,2) - wire_size;Coords_cell(5,1) = x_ini;Coords_cell(5,2) = y_ini;end