%% Creates mesh points for Superfish implementation (no hole)function [CELL] = create_mesh_points(x_ini, num_cell, wire_size, aper)%wire_size = 0.0025*1.2; %cm 20% larger wires...%aper = 0.0483*1.2; %cm%num_cell = 590;% Create y levels for all cells and apertures%x_ini = 0.05;  %cmy_level_cell = zeros(1,num_cell);y_level_aper = zeros(1,num_cell);y_level_aper(1) = wire_size;%disp('Levels -- OK')for i=1:num_cell-1	y_level_cell(i+1) = y_level_aper(i) + aper;	y_level_aper(i+1) = y_level_cell(i+1) + wire_size;end% Call the cell and aper functions subsequently to create the latticefor i=1:num_cell    CELL(i).Coords_cell=mycell(x_ini, y_level_cell(i),wire_size);endfor i=1:num_cell	APER(i).Coords_aper=myaper(x_ini, y_level_aper(i),aper, wire_size);endend % END OF FUNCTION